import React, { Component } from 'react';
import * as emailjs from "emailjs-com";

const EmailJSID = process.env.REACT_APP_EMAILJS_USERID;
const Email_address = process.env.TO_NAME;


class Contact extends Component {
    constructor(props) {
    super(props);

    this.state = {
        name: "",
        email: "",
        feedback: "",
        };
    }

    handleInputChange(event) {
      event.preventDefault();
      const target = event.target;
      const name = target.name;
      const value = target.value;

      this.setState({ [name]: value });
    }

    sendMessage(event) {
        event.preventDefault();

        const templateParams = {
          from_name: this.state.name + " (" + this.state.email + ")",
          to_name: `${Email_address}`,
          feedback: this.state.feedback
        };

        emailjs
          .send("gmail", "gretchen_personal_portfolio", templateParams,`${EmailJSID}`)
          .then(
            function(response) {
              console.log("EMAIL SUCCESSFULLY SENT", response.status, response.text);
            },
            function(error) {
              console.log("EMAIL DID NOT SEND!", error);
            }
          );

        this.setState({
            name: "",
            email: "",
            feedback: ""
            });
        }

render() {
  return (
    <main id="main-contact">
        <div className="page-header">contact.</div>
        <p id="contact-page-text"></p>

        <div className="icons">

        <a href="https://github.com/Gkkrammes" rel="noopener noreferrer" target="_blank">
        </a> 

        <a href="https://www.linkedin.com/in/gretchen-krammes/" rel="noopener noreferrer" target="_blank">
         </a>

        </div>

          <div className="contact-form">
            <form
              className="ui-form"
              id={this.props.id}
              name={this.props.name}
              method={this.props.method}
              action={this.props.action}
              style={{display: "block", textAlign:"center" }}
            >
              <textarea
                  id="name"
                  name="name"
                  onChange={this.handleInputChange.bind(this)}
                  placeholder="your name"
                  required
                  value={this.state.name}
                  style={{ width: "80%", borderRadius:".5rem", padding:"10px", paddingLeft:"20px"}}
                  rows={1}
              />
              <br />
              <textarea
                  id="email"
                  name="email"
                  onChange={this.handleInputChange.bind(this)}
                  placeholder="your email address"
                  required
                  value={this.state.email}
                  style={{ width: "80%", borderRadius:".5rem", padding:"10px", paddingLeft:"20px"}}
                  rows={1}
              />
              <br />
              <textarea
                  id="feedback"
                  name="feedback"
                  onChange={this.handleInputChange.bind(this)}
                  placeholder="your message..."
                  required
                  value={this.state.feedback}
                  style={{ width: "80%", borderRadius:".5rem", padding:"10px", paddingLeft:"20px", height:"400px"}}/>
              <br />
              <input
                  type="button"
                  value=" Send "
                  id="contact-button"
                  className="uibutton"
                  onClick={this.sendMessage.bind(this)}
              />
        </form>
      </div>
    </main>
    );
  };
}

export default Contact;
